import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { withRouter } from "react-router-dom";
//import createSocketConnectionInstance from "./Connection"
import Peer from "peerjs";
//import { validate as uuidValidate } from "uuid";

const myPeer = new Peer(undefined, {
  host: "/",
  port: "3008",
});

const Videochatroom = (props) => {
  // let socketInstance = useRef(null);
  const [socket, setSocket] = useState(null);
  const [peers, addpeers] = useState({});
  const [mystream, setstream] = useState(null);
  const [mypeer, setmypeer] = useState(myPeer);

  useEffect(() => {
    console.log(props.match.params.id);
    console.log("mount it using useEffect!");

    const newsocket = io.connect("http://localhost:4000", {
      query: {
        roomId: props.match.params.id,
      },
    });
    setSocket(newsocket);

    const myVideo = document.createElement("video");
    myVideo.muted = true;

    console.log("alright now ");
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true,
      })
      .then((stream) => {
        setstream(stream);
        addVideoStream(myVideo, stream);

        myPeer.on("open", (id) => {
          console.log("user id send to join room " + id);
          newsocket.emit("join-room", { id, stream });
        });
        myPeer.on("call", (call) => {
          console.log("someone calling");
          call.answer(stream);
          const video = document.createElement("video");
          call.on("stream", (userVideoStream) => {
            addVideoStream(video, userVideoStream);
          });
        });

        newsocket.on("user-connected", (userdata) => {
          console.log("user id received of new user" + userdata.userId);
          // connectToNewUser(userId, mystream);
          setTimeout(connectToNewUser, 1000, userdata, mystream);
        });
        newsocket.on("user-disconnected", (userId) => {
          if (peers[userId]) peers[userId].close();
        });
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const connectToNewUser = (userdata, stream) => {
    console.log("connecting to newuser! ");

    //const call = mypeer.call(userdata.userId, stream);
    console.log("cALL " + "peer " + mypeer.id);
    // call.on("error", (error) => {
    //   console.log(error);
    // });
    // adding peers
    // console.log("peers adding ");
    // addpeers((prevstate) => ({
    //   ...prevstate,
    //   [userId]: call,
    // }));

    // const updatedValue = {};
    // updatedValue[userId] = call;
    // addpeers({
    //   ...peers,
    //   ...updatedValue,
    // });
    // console.log("peers ", peers[userId]);

    const video = document.createElement("video");

    addVideoStream(video, userdata.stream);

    // call.on("stream", (userVideoStream) => {
    //   console.log("streaming ");
    //   addVideoStream(video, userVideoStream);
    // });
    // call.on("close", () => {
    //   video.remove();
    // });
  };

  const addVideoStream = (video, stream) => {
    console.log("adding video stream");
    const videoGrid = document.getElementById("video-grid");
    video.srcObject = stream;
    video.addEventListener("loadedmetadata", () => {
      video.play();
    });
    videoGrid.append(video);
    console.log(":appending");
  };

  return (
    <div id="video-grid">
      <p>video chat room</p>

      <video>Start streaming</video>
      {/* {chatroom} */}
    </div>
  );
};
export default withRouter(Videochatroom);
