import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { withRouter } from "react-router";
import Peer from "peerjs";

import Videochatroom from "./Videochatroom";
import Commonroom from "./Commonroom";
import Temp2 from "./temp2";

const JoinRoom = (props) => {
  const [videoroom, gotovideoroom] = useState(0);
  const [socket, setSocket] = useState(null);
  const [mypeer, setmypeer] = useState("");
  const [copied, setcopied] = useState(false);

  useEffect(() => {
    const myPeer = new Peer(undefined, {
      host: "/",
      port: "3008",
    });

    setmypeer(myPeer);

    console.log(props.match.params.id);
    console.log("mount it using useEffect!");

    const newsocket = io.connect("http://localhost:4000", {
      query: {
        roomId: props.match.params.id,
      },
    });
    setSocket(newsocket);

    myPeer.on("open", (id) => {
      console.log("user id send to join room " + id);
      newsocket.emit("add-to-room", id);
    });
    return () => {
      props.newsocket.emit("user-disconnected", props.myPeer.id);
      props.newsocket.removeAllListeners();

      props.myPeer.disconnect();
    };
  }, []);

  const Joinvideochat = () => {
    console.log("props join room " + props.match.params.id);
    if (videoroom == 0) gotovideoroom(1);
    else gotovideoroom(0);
  };
  const copyRoomId = () => {
    var tempInput = document.createElement("input");
    tempInput.value = props.match.params.id;

    document.body.appendChild(tempInput);
    tempInput.select();
    document.execCommand("copy");
    document.body.removeChild(tempInput);

    setcopied(true);
  };

  return (
    <>
      <header>
        <nav>
          <ul onClick={copyRoomId} onMouseOut={(_) => setcopied(false)}>
            <span className="tooltip">
              {copied ? "Copied!" : "Copy to clipboard"}
            </span>
            <li>Room ID: {props.match.params.id} </li>
          </ul>
        </nav>
      </header>
      <p>Join for a video chat</p>
      <button style={{ width: "50px" }} onClick={Joinvideochat}></button>
      {videoroom === 0 ? (
        <Commonroom newsocket={socket} myPeer={mypeer}></Commonroom>
      ) : (
        <Videochatroom newsocket={socket} myPeer={mypeer}></Videochatroom>
      )}
    </>
  );
};
export default withRouter(JoinRoom);
