import React, { useEffect } from "react";

const Chatbox = () => {
  useEffect(() => {
    scrollToBottom();
  }, []);
  const scrollToBottom = () => {
    const drawer = document.getElementById("chatbox");
    drawer.scrollIntoView({ behavior: "smooth" });
  };
  const sendMessage = () => {};
  return (
    <div className="chatbox" id="chatbox">
      <div id={this.props.id} className="drawer">
        <div className="drawer-header">
          <div className="drawer-title">{this.props.title}</div>
          {this.props.headerContent}
          <IoCloseOutline
            className="drawer-button"
            onClick={this.close}
            size="30"
          />
        </div>

        <div className="list-wrapper">
          {this.props.content}
          <div className="list-end"></div>
        </div>

        <div id="chat-input-wrapper">
          <form onSubmit={sendMessage}>
            <input id="chat-input" />

            <button id="chat-send-button" type="submit" onClick={sendMessage}>
              <i
                class="fa fa-angle-double-left"
                style={{ fontSize: "24px", color: "yellow" }}
              />
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Chatbox;
