import React, { useEffect } from "react";
import ChatMessages from "./ChatMessages";

const Chatbox = (props) => {
  useEffect(() => {
    if (props.socket) {
      props.socket.on("messageSend", (data) => {
        console.log("mesagesend emitted ");
        const div = document.getElementsByClassName("list-wrapper");
        const chats = <ChatMessages />;
        div.append(chats);
      });
    }
  }, [props]);
  const sendMessage = (event) => {
    event.preventDefault();
    console.log("chatbox socket " + props.socket);

    const input = document.getElementById("chat-input");
    const inputValue = input.value;
    input.value = "";

    if (inputValue !== "") {
      props.socket.emit("messageSend", {
        message: inputValue,
        timeStamp: Date.now(),
      });
    }
  };
  return (
    <div className="chatbox" id="chatbox">
      <div className="chat-header">Chatbox</div>
      <div className="list-wrapper">
        all messages
        <div className="list-end"></div>
      </div>

      <div class="chat-form-wrapper">
        <form onSubmit={sendMessage}>
          <input
            type="text"
            placeholder=" Write a message...."
            id="chat-input"
          />
        </form>
      </div>
    </div>
  );
};

export default Chatbox;
