import React from "react";

export default function Commonroom() {
  useEffect(() => {
    const myPeer = new Peer(undefined, {
      host: "/",
      port: "3008",
    });
    let mystream = "";
    //setmypeer(myPeer);

    console.log(props.match.params.id);
    console.log("mount it using useEffect!");

    const newsocket = io.connect("http://localhost:4000", {
      query: {
        roomId: props.match.params.id,
      },
    });
    console.log("socket id " + newsocket.id);
    setSocket(newsocket);
    myPeer.on("open", (id) => {
      console.log("user id send to join room " + id);

      setmypeerID(id);
      newsocket.emit("join-room", id);
    });
  });
  const startvideo = (e) => {
    console.log("uploaded");
    if (e.target.files.length == 0) {
      console.log("empty");
    }

    let file = e.target.files[0];
    const video = document.getElementById("uploaded-video");
    video.src = URL.createObjectURL(file);

    let type = file.type;
    if (!video.canPlayType(type)) {
      alert("cannot play that file");
      return;
    }

    video.play().then(() => {
      if (typeof video.mozCaptureStream == "function") {
        window.localVideoStream = video.mozCaptureStream();
      } else if (typeof video.captureStream == "function") {
        window.localVideoStream = video.captureStream();
      }
    });
  };
  return (
    <div>
      <input
        type="file"
        accept="video/*'"
        onChange={startvideo}
        id="uploaded-file"
      ></input>
      <video id="uploaded-video" autoPlay controls></video>
    </div>
  );
}
