import React, { useState, useEffect } from 'react';
import { io } from "socket.io-client";
import createSocketConnectionInstance from "./Connection"
//import { validate as uuidValidate } from "uuid";
    



export default function JoinRoom(props)
{  //socket.emit('join-room',{})
  // let socketInstance = useRef(null);
   const  [socket,setSocket]=useState("");
   
   const myPeer = new Peer(undefined, {
    host: '/',
    port: '3001'
  })

    useEffect(() => {
        console.log(props.match.params.id);
        console.log('mount it using useEffect!');
           const newsocket =  io.connect("http://localhost:4000", {
            query: {
              roomId: props.match.params.id,
              
            },      
          });
          setSocket(newsocket);
         // startConnection();
       
      }, []);

     
      const startConnection = () => {
        const params = {quality: 12}
        socketInstance.current = createSocketConnectionInstance({
            params
        });
    }
    const videochat=async()=>{
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: true,
        video: { width: 800, height: 300 }
      })
      socket.emit("showVideo",stream);
    
     
      socket.on("showVideo",(stream)=>{
        console.log("showing video");
        const videogrid=document.getElementById('video-grid');
        var video = document.querySelector('video');
        video.srcObject = stream;
        video.addEventListener('loadedmetadata', () => {
        video.play()
        })
        videogrid.append(video);
      })

           
    }
    
     
   
  

    return(<div id='video-grid'>
        
        <button onClick={videochat}>start video chat</button>
        <video>Start streaming</video>
    </div>)
}
