import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { withRouter } from "react-router-dom";
import Peer from "peerjs";

//import { validate as uuidValidate } from "uuid";

const Videochatroom = (props) => {
  // let socketInstance = useRef(null);

  const [socket, setSocket] = useState(null);
  const [peers, addpeers] = useState({});
  const [mystream, setstream] = useState(null);
  const [mypeer, setmypeer] = useState("");

  useEffect(() => {
    const myPeer = new Peer(undefined, {
      host: "/",
      port: "3008",
    });

    setmypeer(myPeer);

    console.log(props.match.params.id);
    console.log("mount it using useEffect!");

    const newsocket = io.connect("http://localhost:4000", {
      query: {
        roomId: props.match.params.id,
      },
    });
    setSocket(newsocket);
    myPeer.on("open", (id) => {
      console.log("user id send to join room " + id);
      newsocket.emit("join-room", id);
    });

    const myVideo = document.getElementById("myvideo");
    myVideo.muted = true;

    console.log("alright now ");
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true,
      })
      .then((stream) => {
        setstream(stream);
        addVideoStream(myVideo, stream);

        newsocket.on("user-connected", (userId) => {
          console.log("user id received of new user" + userId);

          connectToNewUser(userId, stream, myPeer);
        });
        newsocket.on("user-disconnected", (userId) => {
          if (peers[userId]) peers[userId].close();
        });
        myPeer.on("call", (call) => {
          console.log("someone calling");
          call.answer(stream);
          const video = document.createElement("video");
          call.on("stream", (userVideoStream) => {
            addVideoStream(video, userVideoStream);
          });
        });
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const connectToNewUser = (userId, stream, myPeer) => {
    console.log("connecting to newuser! ");

    const call = myPeer.call(userId, stream);
    console.log("cALL " + call + "peer " + myPeer.id);
    call.on("error", (error) => {
      console.log(error);
    });
    //adding peers
    console.log("peers adding ");
    addpeers((prevstate) => ({
      ...prevstate,
      [userId]: call,
    }));

    // const updatedValue = {};
    // updatedValue[userId] = call;
    // addpeers({
    //   ...peers,
    //   ...updatedValue,
    // });
    // console.log("peers ", peers[userId]);

    const video = document.createElement("video");
    console.log("creating element ");
    call.on("stream", (userVideoStream) => {
      console.log("streaming ");
      addVideoStream(video, userVideoStream);
    });
    call.on("close", () => {
      video.remove();
    });
  };

  const addVideoStream = (video, stream) => {
    console.log("adding video stream");
    const videoGrid = document.getElementById("video-grid");
    video.srcObject = stream;
    video.addEventListener("loadedmetadata", () => {
      video.play();
    });
    videoGrid.append(video);
    console.log(":appending");
  };
  const muteunmute = () => {
    const v = document.getElementById("myvideo");
    console.log("muting " + mypeer.id + " socket  " + socket);
    if (v.muted) {
      v.muted = false;
      socket.emit("mute", { muted: false, id: mypeer.id });
    } else {
      v.muted = true;
      socket.emit("mute", { muted: true, id: mypeer.id });
    }
  };

  return (
    <div id="video-grid">
      <p>video chat room</p>

      <video id="myvideo" />
      <button onClick={muteunmute}>pause</button>
    </div>
  );
};
export default withRouter(Videochatroom);
