import React, { useEffect } from "react";

const Chatbox = () => {
  useEffect(() => {
    scrollToBottom();
  }, []);
  const scrollToBottom = () => {
    const drawer = document.getElementById("chatbox");
    drawer.scrollIntoView({ behavior: "smooth" });
  };
  return (
    <div className="chatbox" id="chatbox">
      <p>hello</p>
    </div>
  );
};

export default Chatbox;
