import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { withRouter } from "react-router-dom";
//import createSocketConnectionInstance from "./Connection"
import Peer from "peerjs";
//import { validate as uuidValidate } from "uuid";

const JoinRoom=(props)=> {
  // let socketInstance = useRef(null);
  const [socket, setSocket] = useState("");
  const [peers, addpeers] = useState({});

  const myPeer = new Peer(undefined, {
    host: "/",
    port: "3001",
  });

  useEffect(() => {
    console.log(props.match.params.id);
    console.log("mount it using useEffect!");
    const newsocket = io.connect("http://localhost:4000", {
      query: {
        roomId: props.match.params.id,
      },
    });
    setSocket(newsocket);

   
    newsocket.on("user-disconnected", (userId) => {
      if (peers[userId]) peers[userId].close();
    });

    myPeer.on("open", (id) => {
      newsocket.emit("join-room", id);
    });
  }, [peers,socket,myPeer,props]);

  const startVideo = () => {
    console.log("video started ");

    const myVideo = document.createElement("video");
    myVideo.muted = true;
  
  console.log("alright now ");
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true,
      })
      .then((stream) => {
        addVideoStream(myVideo, stream);

        myPeer.on("call", (call) => {
          call.answer(stream);
          const video = document.createElement("video");
          call.on("stream", (userVideoStream) => {
            addVideoStream(video, userVideoStream);
          });
        });
        socket.on("user-connected", (userId) => {
          connectToNewUser(userId, stream);
        });

       
      });

  

    const connectToNewUser=(userId, stream)=> {
      console.log("connecting to user ");
      const call = myPeer.call(userId, stream);
      const video = document.createElement("video");
      call.on("stream", (userVideoStream) => {
        addVideoStream(video, userVideoStream);
      });
      call.on("close", () => {
        video.remove();
      });
      addpeers((prevstate)=>({
        ...prevstate,
        [userId]:call
      }))
      //peers[userId] = call;
    }

    const addVideoStream=(video, stream)=> {
      console.log("adding video stream");
      const videoGrid = document.getElementById("video-grid");
      video.srcObject = stream;
      video.addEventListener("loadedmetadata", () => {
        video.play();
      });
      videoGrid.append(video);
    }
  };

  

  return (
    <div id="video-grid">
      <p>hello there </p>
      <button onClick={startVideo}>start video chat</button>
      <video >Start streaming</video>
    </div>
  );
}
export default withRouter(JoinRoom)
