import React, { useState, useEffect } from "react";

const Commonroom = (props) => {
  const [connection, setconnection] = useState(null);
  let localVideoStream = "";
  useEffect(() => {
    console.log("notsocket ");
    if (props.newsocket) {
      props.newsocket.emit("videostatus");
      props.newsocket.on("videostatus", (data) => {
        if (data.video === true) {
          props.myPeer.call(data.id);
        }
      });
      props.myPeer.on("call", () => {});

      // console.log("found socket ");
      // props.newsocket.on("play-video", (peerdata) => {
      //   console.log("peerdata " + peerdata);
      //   peerdata.forEach((peer) => {
      // const con = props.myPeer.connect(peer);
      // con.send("hello");
      // props.myPeer.on("connection", () => {
      //   console.log("connection");
      //   con.on("open", () => {
      //     console.log("open");
      //     con.on("data", (data) => {
      //       console.log("video data " + data);
      //     });
      //   });
      // });
      // });
      // });
    }
  }, [props]);
  const startvideo = (e) => {
    console.log("uploaded");
    if (e.target.files.length == 0) {
      console.log("empty");
    }

    let file = e.target.files[0];
    const video = document.getElementById("uploaded-video");
    video.src = URL.createObjectURL(file);

    let type = file.type;
    if (!video.canPlayType(type)) {
      alert("cannot play that file");
      return;
    }

    video.play().then(() => {
      if (typeof video.mozCaptureStream == "function") {
        localVideoStream = video.mozCaptureStream();
      } else if (typeof video.captureStream == "function") {
        localVideoStream = video.captureStream();
      }
      props.newsocket.emit("play-video");
    });
  };
  return (
    <div>
      <input
        type="file"
        accept="video/*'"
        onChange={startvideo}
        id="uploaded-file"
      ></input>
      <video id="uploaded-video" autoPlay controls></video>
    </div>
  );
};
export default Commonroom;
