import React from "react";

export default function Commonroom(props) {
  const startvideo = (e) => {
    console.log("uploaded");
    if (e.target.files.length == 0) {
      console.log("empty");
    }

    let file = e.target.files[0];
    const video = document.getElementById("uploaded-video");
    video.src = URL.createObjectURL(file);

    let type = file.type;
    if (!video.canPlayType(type)) {
      alert("cannot play that file");
      return;
    }
    const localVideoStream = "";
    video.play().then(() => {
      if (typeof video.mozCaptureStream == "function") {
        localVideoStream = video.mozCaptureStream();
      } else if (typeof video.captureStream == "function") {
        localVideoStream = video.captureStream();
      }
    });
    props.newsocket.on("play-video", (peerdata) => {
      peerdata.forEach((peer) => {
        props.myPeer.connect(peer.peerid);
      });
    });
  };
  return (
    <div>
      <input
        type="file"
        accept="video/*'"
        onChange={startvideo}
        id="uploaded-file"
      ></input>
      <video id="uploaded-video" autoPlay controls></video>
      <button onclick={this.shareVideo}>
        share video through screenshare stream
      </button>
    </div>
  );
}
