import React from "react";
import Popup from "./Popup";

export default function Commonroom() {
  const startvideo = (e) => {
    console.log("uploaded");
    if (e.target.files.length == 0) {
      console.log("empty");
    }

    let file = e.target.files[0];
    const video = document.getElementById("uploaded-video");
    video.src = URL.createObjectURL(file);

    let type = file.type;
    if (!video.canPlayType(type)) {
      alert("cannot play that file");
      return;
    }

    video.play().then(() => {
      if (typeof video.mozCaptureStream == "function") {
        window.localVideoStream = video.mozCaptureStream();
      } else if (typeof video.captureStream == "function") {
        window.localVideoStream = video.captureStream();
      }
    });
  };
  const handlechange = (e) => {
    const username = e.target.value;
    if (username.length > 0) {
      localStorage.setItem("userName", username);
      //   props.onSubmit();
    }
  };
  const handlesubmit = () => {
    e.preventDefault();
  };
  return (
    <div style={{ marginTop: "100px" }}>
      <form>
        <label>enter name</label>
        <input type="name" onChange={handlechange} />

        <input type="submit" value="submit" onSubmit={handlesubmit}></input>
      </form>
      {/* <input
        type="file"
        accept="video/*'"
        onChange={startvideo}
        id="uploaded-file"
      ></input>
      <video id="uploaded-video" autoPlay controls></video> */}
    </div>
  );
}
