import React from "react";

export default function Commonroom() {
  const startvideo = () => {
    const file = document.getElementById("uploaded-file");
    const video = document.getElementById("uploaded-video");
    var stream = file.captureStream(25);
    // video.srcObject = stream;
    video.srcObject = URL.createObjectURL(file);
  };
  return (
    <div>
      <input
        type="file"
        accept="video/*'"
        onChange={startvideo}
        id="uploaded-file"
      ></input>
      <video id="uploaded-video"></video>
    </div>
  );
}
