import React, { useState, useEffect } from "react";

const Commonroom = (props) => {
  const [connection, setconnection] = useState(null);
  // const [localVideoStream, setstream] = useState(null);
  let localVideoStream = "";
  useEffect(() => {
    console.log("notsocket ");
    if (props.newsocket) {
      // props.newsocket.on("play-video", (data) => {
      //   if (props.myPeer.id === data.peerid) {
      //     console.log("peers " + data.peers);
      //     data.peers.forEach((peer) => {
      //       if (peer != props.myPeer.id) {
      //         console.log("videostream " + localVideoStream);
      //         const video = document.getElementById("uploaded-video");
      //         props.myPeer.call(peer, video.captureStream());
      //       }
      //     });
      //   }
      // });

      props.myPeer.on("call", (call) => {
        console.log("someone calling");
        navigator.mediaDevices
          .getUserMedia({
            video: true,
            audio: true,
          })
          .then((stream) => {
            call.answer(stream);
          });

        const video = document.getElementById("uploaded-video");

        call.on("stream", (userVideoStream) => {
          console.log("streaming ");
          addVideoStream(video, userVideoStream);
        });
      });
    }
  }, [props]);

  const addVideoStream = (video, stream) => {
    if (video) {
      video.srcObject = stream;
      video.addEventListener("loadedmetadata", () => {
        video.play();
      });
    }
  };
  const startvideo = (e) => {
    console.log("uploaded");
    if (e.target.files.length == 0) {
      console.log("empty");
    }

    let file = e.target.files[0];
    const video = document.getElementById("uploaded-video");
    video.src = URL.createObjectURL(file);

    let type = file.type;
    if (!video.canPlayType(type)) {
      // alert("cannot play that file");
      console.log("cannot play that file");
      return;
    }

    video.play().then(() => {
      if (typeof video.mozCaptureStream == "function") {
        localVideoStream = video.mozCaptureStream();
      } else if (typeof video.captureStream == "function") {
        localVideoStream = video.captureStream();
      }

      props.newsocket.emit("play-video", props.myPeer.id);

      props.newsocket.on("play-video", (data) => {
        if (props.myPeer.id === data.peerid) {
          data.peers.forEach((peer) => {
            if (peer != props.myPeer.id) {
              props.myPeer.connect(peer);
              console.log("videostream " + localVideoStream);
              props.myPeer.call(peer, localVideoStream);
            }
          });
        }
      });
    });
  };
  return (
    <div>
      <input
        type="file"
        accept="video/*'"
        onChange={startvideo}
        id="uploaded-file"
      ></input>
      <video id="uploaded-video" autoPlay controls muted></video>
      <video id="new" controls muted></video>
    </div>
  );
};
export default Commonroom;
