import React, { useState, useEffect, Component } from "react";
import { io } from "socket.io-client";
import { withRouter } from "react-router-dom";
import Peer from "peerjs";

const initializePeerConnection = () => {
  const myPeer = new Peer(undefined, {
    host: "/",
    port: "3008",
  });
  return myPeer;
};
const initializeSocketConnection = async (props) => {
  const newsocket = io.connect("http://localhost:4000", {
    query: {
      roomId: props,
    },
  });
  return newsocket;
};
export default class Videochatroom extends Component {
  myPeer;

  peers;
  state = {
    socket: null,
  };
  constructor(props) {
    super(props);

    console.log("props " + this.props.roomid);
    this.myPeer = initializePeerConnection();
    const s = initializeSocketConnection(this.props.roomid);
    this.setState({
      socket: s,
    });
    this.initializeSocketEvents();
    this.initializePeersEvents();
  }

  initializeSocketEvents = () => {
    this.state.socket.on("connect", () => {
      console.log("socket connected");
    });

    this.state.socket.on("error", (err) => {
      console.log("socket error --", err);
    });
  };
  initializePeersEvents = () => {
    this.myPeer.on("open", (id) => {
      console.log("user id send to join room " + id);
      this.state.socket.emit("join-room", id);
      this.setNavigatorToStream();
    });
  };
  setNavigatorToStream = () => {
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true,
      })
      .then((stream) => {
        if (stream) {
          const myVideo = document.getElementById("myvideo");
          myVideo.muted = true;
          this.addVideoStream(myVideo, stream);
          this.setPeersListeners(stream);
          this.newUserConnection(stream);
        }
      });
  };

  setPeersListeners = (stream) => {
    this.myPeer.on("call", (call) => {
      console.log("someone calling");

      call.answer(stream);
      const video = document.createElement("video");
      console.log("call.peer " + call.peer);
      video.id = call.peer;

      call.on("stream", (userVideoStream) => {
        this.addVideoStream(video, userVideoStream);
      });

      call.on("close", () => {
        // myVideo.remove();
      });
    });
  };
  newUserConnection = (stream) => {
    this.socket.on("user-connected", (userId) => {
      console.log("user id received of new user" + userId);

      this.connectToNewUser(userId, stream, this.myPeer);
    });
  };
  connectToNewUser = (userId, stream, myPeer) => {
    console.log("connecting to newuser! ");

    const call = this.myPeer.call(userId, stream);

    call.on("error", (error) => {
      console.log(error);
    });
    // adding peers flush updates
    // console.log("peers adding ");
    // addpeers((prevstate) => ({
    //   ...prevstate,
    //   [userId]: call,
    // }));

    // const updatedValue = {};
    // updatedValue[userId] = call;
    // addpeers({
    //   ...peers,
    //   ...updatedValue,
    // });
    // console.log("peers ", peers[userId]);

    const video = document.createElement("video");
    video.id = userId;
    console.log("cALL " + call + "peer " + myPeer.id);
    if (call === null || call === "" || call === undefined) console.log("null");

    call.on("stream", (userVideoStream) => {
      console.log("streaming ");
      this.addVideoStream(video, userVideoStream);
    });

    call.on("close", () => {
      console.log("call closing ");
      video.remove();
    });
    console.log("close");
  };
  addVideoStream = (video, stream) => {
    console.log("adding video stream with id ", video.id);

    const videoGrid = document.getElementById("video-grid");
    video.srcObject = stream;
    video.addEventListener("loadedmetadata", () => {
      video.play();
    });
    videoGrid.append(video);

    console.log(":appending");
  };

  render() {
    return (
      <div>
        <div id="video-grid">
          <p>video chat room</p>
          <video id="myvideo" />
          <button>pause</button>
        </div>
      </div>
    );
  }
}
