import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";
import { withRouter } from "react-router-dom";
import Peer from "peerjs";

//import { validate as uuidValidate } from "uuid";

const Videochatroom = (props) => {
  // let socketInstance = useRef(null);

  const [socket, setSocket] = useState(null);
  const [peers, addpeers] = useState({});
  const [mypeerID, setmypeerID] = useState("");
  const [mystreams, setmystream] = useState("");

  useEffect(() => {
    let mystream = "";
    //setmypeer(myPeer);

    setSocket(props.newsocket);

    props.newsocket.emit("join-room", props.myPeer.id);

    const myVideo = document.getElementById("myvideo");
    myVideo.muted = true;

    console.log("alright now ");
    navigator.mediaDevices
      .getUserMedia({
        video: true,
        audio: true,
      })
      .then((stream) => {
        mystream = stream;
        setmystream(stream);
        addVideoStream(myVideo, stream);

        props.newsocket.on("user-connected", (userId) => {
          console.log("user id received of new user" + userId);
          setTimeout(connectToNewUser, 1000, userId, stream, props.myPeer);
          // connectToNewUser(userId, stream, myPeer);
        });
        props.newsocket.on("user-disconnected", (userId) => {
          console.log("userid disconnected " + userId);
          const disconnectedVideo = document.getElementById(userId);
          if (userId) {
            disconnectedVideo.remove();
          }

          if (peers[userId]) peers[userId].close();
        });
        props.newsocket.on("mute-unmute", (data) => {
          if (data.id != props.myPeer.id) {
            const peervideo = document.getElementById(data.id);
            if (data.mute) peervideo.muted = true;
            else peervideo.muted = false;
          }
        });

        props.myPeer.on("call", (call) => {
          console.log("someone calling");

          call.answer(stream);
          const video = document.createElement("video");
          console.log("call.peer " + call.peer);
          video.id = call.peer;
          video.muted = true;

          call.on("stream", (userVideoStream) => {
            addVideoStream(video, userVideoStream);
          });

          call.on("close", () => {
            console.log("call closing");
            myVideo.remove();
          });
        });
      });

    // return works like component will unmount
    return () => {
      props.newsocket.emit("user-disconnected", props.myPeer.id);
      props.newsocket.removeAllListeners();

      mystream.getTracks().forEach(function (track) {
        if (track.readyState == "live") {
          track.stop();
        }
      });
      props.myPeer.disconnect();
    };
  }, []);

  const connectToNewUser = async (userId, stream, myPeer) => {
    console.log("connecting to newuser! ");
    const call = await myPeer.call(userId, stream);

    call.on("error", (error) => {
      console.log(error);
    });
    // adding peers flush updates
    console.log("peers adding ");
    addpeers((prevstate) => ({
      ...prevstate,
      [userId]: call,
    }));

    const video = document.createElement("video");
    video.id = userId;
    video.muted = true;
    console.log("cALL " + call + "peer " + myPeer.id);
    if (call === null || call === "" || call === undefined) console.log("null");

    await call.on("stream", (userVideoStream) => {
      console.log("streaming ");
      addVideoStream(video, userVideoStream);
    });
  };

  const addVideoStream = (video, stream) => {
    console.log("adding video stream with id ", video.id);

    const videoGrid = document.getElementById("video-grid");
    video.srcObject = stream;
    video.addEventListener("loadedmetadata", () => {
      video.play();
    });
    videoGrid.append(video);

    console.log(":appending");
  };

  const muteunmute = () => {
    const v = document.getElementById("myvideo");
    const data = {
      id: mypeerID,
    };
    if (v.muted) {
      v.muted = false;
      data.mute = false;
    } else {
      v.muted = true;
      data.mute = true;
    }
    console.log("mute " + mypeerID);

    socket.emit("mute-unmute", data);
  };
  const togglestream = () => {
    mystreams.getTracks().forEach(function (track) {
      if (track.readyState == "live") {
        console.log(":true ");
        track.stop();
      } else {
        console.log(":false");
        navigator.mediaDevices
          .getUserMedia({
            video: true,
            audio: true,
          })
          .then((stream) => {
            const video = document.getElementById("myvideo");
            video.srcObject = stream;
            video.addEventListener("loadedmetadata", () => {
              video.play();
            });
          });
      }
    });
  };

  return (
    <div id="video-grid">
      <p>video chat room</p>
      <video id="myvideo" />
      <button onClick={muteunmute}>pause</button>
      <button onClick={togglestream}>toggle video</button>
    </div>
  );
};
export default withRouter(Videochatroom);
